package javaprogramday2;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    for (int i=1;i<=10;i++) {
    	System.out.println("Value of i : " + i);
    	
    	for (int j=10;j>=1;j--) {
        	System.out.println("Value of j : " + j);
    }
	}
	}
}
